select t06.civilregionalismid,t06.civilregionalismname,t01.reportid,t03.indexitemcode,t03.indexitemname,t02.indexitemcode,t02.indexitemname,t01.reportitemvalue from historyreportitemvalue t01 left join indexitem t02 on t01.indexitemid=t02.indexitemid left join indexitem t03 on t02.parentid=t03.indexitemid left join historyreport t04 on t01.reportid=t04.reportid left join creditionrelation t05 on t04.creditionrelationid=t05.creditionrelationid left join civilregionalism t06 on t05.civilregionalismid=t06.civilregionalismid where t01.reportid in (select t02.reportid from hjcn1.historyreport t02 left outer join hjcn1.actionlist t03 on t03.actionlistid = t02.actionlistid left outer join hjcn1.reportstage t031 on t031.reportstageid = t03.reportstageid left outer join hjcn1.flowaction t04 on t02.flowactionid = t04.flowactionid left outer join hjcn1.flowactionstatus t041 on t04.flowactionstatusid = t041.flowactionstatusid left outer join hjcn1.disaster t05 on t02.disasterid = t05.disasterid left outer join hjcn1.creditionrelation t051 on t051.creditionrelationid = t05.creditionrelationid left outer join hjcn1.civilregionalism t052 on t051.civilregionalismid = t052.civilregionalismid left outer join hjcn1.civilregionalismlevel t053 on t053.civilregionalismlevelid = t052.civilregionalismlevelid left outer join hjcn1.civilregionalism t054 on t052.parentid=t054.civilregionalismid where 1=1 and t02.actionlistid is not null and t02.flowactionid is not null and t054.parentid is not null and t041.FLOWACTIONSTATUSID = '11113'
and t031.reportstageid=#{reportType,jdbcType=VARCHAR} )
and t02.indexitemname = #{indexName,jdbcType=VARCHAR}
and t06.civilregionalismid=#{civiId,jdbcType=VARCHAR}

select t06.civilregionalismid,t04.reportid,t04.reporttime, count(*) as count,wm_concat(t02.indexitemcode) as codes,wm_concat(t02.indexitemname) as names, wm_concat(replace(t01.reportitemvalue,',','ï¼Œ')) as datas from historyreportitemvalue t01 left join indexitem t02 on t01.indexitemid=t02.indexitemid left join indexitem t03 on t02.parentid=t03.indexitemid left join historyreport t04 on t01.reportid=t04.reportid left join creditionrelation t05 on t04.creditionrelationid=t05.creditionrelationid left join civilregionalism t06 on t05.civilregionalismid=t06.civilregionalismid where 1=1 and t01.reportitemvalue is not null and t01.reportid in (select t02.reportid from hjcn1.historyreport t02 left outer join hjcn1.actionlist t03 on t03.actionlistid = t02.actionlistid left outer join hjcn1.reportstage t031 on t031.reportstageid = t03.reportstageid left outer join hjcn1.flowaction t04 on t02.flowactionid = t04.flowactionid left outer join hjcn1.flowactionstatus t041 on t04.flowactionstatusid = t041.flowactionstatusid left outer join hjcn1.disaster t05 on t02.disasterid = t05.disasterid left outer join hjcn1.creditionrelation t051 on t051.creditionrelationid = t05.creditionrelationid left outer join hjcn1.civilregionalism t052 on t051.civilregionalismid = t052.civilregionalismid left outer join hjcn1.civilregionalismlevel t053 on t053.civilregionalismlevelid = t052.civilregionalismlevelid left outer join hjcn1.civilregionalism t054 on t052.parentid=t054.civilregionalismid where 1=1 and t02.actionlistid is not null and t02.flowactionid is not null and t054.parentid is not null and t041.FLOWACTIONSTATUSID = '11113'
and t05.disasterkindid=#{companyType,jdbcType=VARCHAR}
and t031.reportstageid=#{reportType,jdbcType=VARCHAR} )
and t06.civilregionalismid=#{civiId,jdbcType=VARCHAR}
group by t04.reportid,t04.reporttime,t06.civilregionalismid

